import React, { useState } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [assignedClassId, setAssignedClassId] = useState(null);
  const [classAllowed, setClassAllowed] = useState(true);
  const [dropdownOpened, setDropdownOpened] = useState(false);

  const allClasses = [
    { id: 1, name: 'UKG' },
    { id: 2, name: 'Class 1' },
    { id: 3, name: 'Class 2' },
    { id: 4, name: 'Class 3' },
    { id: 5, name: 'Class 4' },
    { id: 6, name: 'Class 5' },
    { id: 7, name: 'Class 6' },
    { id: 8, name: 'Class 7' },
    { id: 9, name: 'Class 8' },
    { id: 10, name: 'Class 9' },
    { id: 11, name: 'Class 10' },
  ];

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  const fetchAssignedClass = async () => {
    const storedToken = localStorage.getItem('token') || token;
    if (!storedToken) {
      alert('Authentication token missing. Please log in again.');
      return;
    }

    try {
      const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      if (res.data && res.data.length > 0) {
        setAssignedClassId(res.data[0].id);
      } else {
        alert('No assigned class found.');
      }
    } catch (err) {
      console.error('Error fetching assigned class:', err);
      alert('Failed to fetch your assigned class. Please login again.');
    }
  };

  const handleDropdownOpen = () => {
    if (!dropdownOpened) {
      setDropdownOpened(true);
      fetchAssignedClass();
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "student_class") {
      const isValid = Number(value) === Number(assignedClassId);
      setClassAllowed(isValid);
    }

    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.student_class) return alert("Please select a class");
    if (!classAllowed) return alert("❌ You cannot assign students to this class.");

    try {
      const storedToken = localStorage.getItem('token') || token;

      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      alert('✅ Student profile created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });
      setDropdownOpened(false);
      setAssignedClassId(null);
      setClassAllowed(true);

    } catch (error) {
      alert('❌ Failed to create student: ' + JSON.stringify(error.response?.data || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Student</h2>

      <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
      <input type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange} required />

      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>

      <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input type="text" name="parent_contact" placeholder="Parent Contact" value={formData.parent_contact} onChange={handleChange} required />

      <label>
        Select Class:
        <select
          name="student_class"
          value={formData.student_class}
          onChange={handleChange}
          onClick={handleDropdownOpen}
          onFocus={handleDropdownOpen}
          required
        >
          <option value="">-- Select Class --</option>
          {allClasses.map(cls => (
            <option key={cls.id} value={cls.id}>{cls.name}</option>
          ))}
        </select>
      </label>

      {!classAllowed && (
        <p style={{ color: 'red', fontWeight: 'bold' }}>
          ❌ You can only assign students to your assigned class.
        </p>
      )}

      <button type="submit" disabled={loading || !classAllowed}>
        Create Student
      </button>
    </form>
  );
};

export default AddStudent;
