import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [classes, setClasses] = useState([]);
  const [fetched, setFetched] = useState(false); // Prevent multiple API calls
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  // ✅ Fetch classes only when a class is selected and not already fetched
  useEffect(() => {
    const fetchTeacherClasses = async () => {
      const storedToken = localStorage.getItem('token') || token;

      if (!storedToken) {
        alert('Authentication token missing. Please log in again.');
        return;
      }

      setLoading(true);
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000); // 30s timeout

      try {
        console.log('🔐 Using token:', storedToken);
        const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
          headers: { Authorization: `Bearer ${storedToken}` },
          signal: controller.signal,
        });

        setClasses(res.data);
        setFetched(true); // Mark that classes are fetched

      } catch (err) {
        if (axios.isCancel(err)) {
          alert('Request timed out. Try again later.');
        } else if (err.response) {
          console.error('Backend error:', err.response.data);
          if (err.response.status === 401 && err.response.data?.code === "token_not_valid") {
            alert("Session expired or invalid token. Please log in again.");
            localStorage.removeItem('token');
            window.location.href = '/TeacherDashboard';
          } else {
            alert(`Error: ${err.response.data.detail || JSON.stringify(err.response.data)}`);
          }
        } else {
          alert('Unexpected error: ' + err.message);
        }
      } finally {
        clearTimeout(timeoutId);
        setLoading(false);
      }
    };

    if (formData.student_class && !fetched) {
      fetchTeacherClasses();
    }
  }, [formData.student_class, fetched, token]);

  // Timeout warning if loading takes too long
  useEffect(() => {
    const timeout = setTimeout(() => {
      if (loading) {
        setLoading(false);
        alert('Loading took too long. Please try again.');
      }
    }, 20000);
    return () => clearTimeout(timeout);
  }, [loading]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.student_class) {
      return alert("Please select a class");
    }

    const isValidClass = classes.some(cls => cls.id.toString() === formData.student_class);
    if (!isValidClass) {
      return alert("You can only add students to your assigned classes.");
    }

    try {
      const storedToken = localStorage.getItem('token') || token;

      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      alert('✅ Student profile created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });
      setFetched(false); // Allow fetching again if class is selected again

    } catch (error) {
      alert('❌ Failed to create student: ' + JSON.stringify(error.response?.data || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Student</h2>

      <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
      <input type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange} required />

      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>

      <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input type="text" name="parent_contact" placeholder="Parent Contact" value={formData.parent_contact} onChange={handleChange} required />

      <select name="student_class" value={formData.student_class} onChange={handleChange} required>
        <option value="">-- Select Class --</option>
        {classes.map(cls => (
          <option key={cls.id} value={cls.id}>{cls.name}</option>
        ))}
      </select>

      <button type="submit" disabled={loading}>Create Student</button>
    </form>
  );
};

export default AddStudent;
