import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [classes, setClasses] = useState([]);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  useEffect(() => {
    const fetchTeacherClasses = async () => {
      const storedToken = localStorage.getItem('token') || token;
      if (!storedToken) {
        console.warn('Token not found');
        return;
      }

      setLoading(true);
      try {
        const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
          headers: { Authorization: `Bearer ${storedToken}` }
        });
        setClasses(res.data); // only teacher's classrooms
      } catch (err) {
        if (err.response) {
          console.error('Error response:', err.response.data);
          alert(`Error fetching classes: ${err.response.data.detail || JSON.stringify(err.response.data)}`);
        } else if (err.request) {
          console.error('No response received:', err.request);
          alert('No response from server. Please check your network.');
        } else {
          console.error('Error setting up request:', err.message);
          alert('Error fetching classes: ' + err.message);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchTeacherClasses();
  }, [token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.student_class) {
      return alert("Please select a class");
    }

    // Validate if selected class is in the teacher's classroom list
    const isValidClass = classes.some(cls => cls.id.toString() === formData.student_class);
    if (!isValidClass) {
      return alert("Invalid class selection. You can only add students to your assigned classes.");
    }

    try {
      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Student profile created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });
    } catch (error) {
      alert('Failed to create student: ' + JSON.stringify(error.response?.data || error.message));
    }
  };

  if (loading) return <p>Loading classes...</p>;

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="username"
        value={formData.username}
        onChange={handleChange}
        placeholder="Username"
        required
      />
      {/* Add other fields like email, password, etc. here */}

      <label>
        Select Class:
        <select
          name="student_class"
          value={formData.student_class}
          onChange={handleChange}
          required
        >
          <option value="">-- Select Class --</option>
          {classes.map(cls => (
            <option key={cls.id} value={cls.id}>
              {cls.name}
            </option>
          ))}
        </select>
      </label>

      <button type="submit">Add Student</button>
    </form>
  );
};

export default AddStudent;
