import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [classes, setClasses] = useState([]);
  const [classAllowed, setClassAllowed] = useState(true);
  const [dropdownOpened, setDropdownOpened] = useState(false);

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  // 🔁 Reset dropdown state when formData is cleared
  useEffect(() => {
    if (
      formData.username === '' &&
      formData.email === '' &&
      formData.password === '' &&
      formData.student_class === ''
    ) {
      setDropdownOpened(false);
      setClasses([]);
    }
  }, [formData]);

  const fetchTeacherClasses = async () => {
    const storedToken = localStorage.getItem('token') || token;

    if (!storedToken) {
      alert('Authentication token missing. Please log in again.');
      return;
    }

    try {
      console.log('🔐 Using token to fetch class list:', storedToken);

      const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      console.log('✅ Classrooms fetched:', res.data);

      if (Array.isArray(res.data) && res.data.length > 0) {
        setClasses(res.data);
      } else {
        alert("⚠️ No classrooms assigned to you.");
      }

    } catch (err) {
      console.error("❌ Error while fetching classrooms:", err.response?.data || err.message);

      if (err.response?.status === 401) {
        alert("Session expired. Please login again.");
        localStorage.removeItem('token');
        window.location.href = '/TeacherDashboard';
      } else {
        alert(`Error fetching classrooms: ${err.response?.data?.detail || err.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDropdownOpen = () => {
    if (!dropdownOpened) {
      setDropdownOpened(true);
      setLoading(true);
      fetchTeacherClasses();
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "student_class") {
      const isValid = classes.some(cls => cls.id.toString() === value);
      setClassAllowed(isValid);
    }

    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.student_class) {
      return alert("Please select a class");
    }

    if (!classAllowed) {
      return alert("❌ You are not allowed to assign students to this class.");
    }

    try {
      const storedToken = localStorage.getItem('token') || token;

      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      alert('✅ Student profile created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });

    } catch (error) {
      console.error("❌ Failed to create student:", error.response?.data || error.message);
      alert('❌ Failed to create student: ' + JSON.stringify(error.response?.data || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Student</h2>

      <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
      <input type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange} required />

      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>

      <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input type="text" name="parent_contact" placeholder="Parent Contact" value={formData.parent_contact} onChange={handleChange} required />

      <label>
        Select Class:
        <select
          name="student_class"
          value={formData.student_class}
          onChange={handleChange}
          onClick={handleDropdownOpen}
          onFocus={handleDropdownOpen}
          required
        >
          <option value="">-- Select Class --</option>
          {classes.length === 0 && <option disabled>Loading or no classes available</option>}
          {classes.map(cls => (
            <option key={cls.id} value={cls.id}>{cls.name}</option>
          ))}
        </select>
      </label>

      {!classAllowed && (
        <p style={{ color: 'red', fontWeight: 'bold' }}>
          ❌ You are not allowed to assign students to this class.
        </p>
      )}

      <button type="submit" disabled={loading || !classAllowed}>
        Create Student
      </button>
    </form>
  );
};

export default AddStudent;
