import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [classes, setClasses] = useState([]);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  useEffect(() => {
  const fetchTeacherClasses = async () => {
    const storedToken = localStorage.getItem('token') || token;

    if (!storedToken) {
      console.warn('❌ Token not found in localStorage or props.');
      alert('Authentication token missing. Please log in again.');
      return;
    }

    setLoading(true);

    const controller = new AbortController();
    const timeoutId = setTimeout(() => {
      controller.abort();
    }, 30000); // Abort after 30 seconds

    try {
      console.log('🔐 Using token:', storedToken);  // Debug token

      const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
        headers: { Authorization: `Bearer ${storedToken}` },
        signal: controller.signal
      });

      setClasses(res.data);  // ✅ Store fetched classrooms

    } catch (err) {
      if (axios.isCancel(err)) {
        console.error('❌ Request timed out.');
        alert('Request timed out. Please try again later.');
      } else if (err.response) {
        console.error('❌ Backend responded with error:', err.response.data);

        if (err.response.status === 401 && err.response.data?.code === "token_not_valid") {
          alert("Your session has expired or token is invalid. Please log in again.");
          localStorage.removeItem('token');
          window.location.href = '/TeacherDashboard'; // Redirect to Teacher Dashboard
        } else {
          alert(`Error fetching classes: ${err.response.data.detail || JSON.stringify(err.response.data)}`);
        }

      } else if (err.request) {
        console.error('❌ No response from backend:', err.request);
        alert('No response from server. Check your network or backend server.');
      } else {
        console.error('❌ Unexpected Axios error:', err.message);
        alert('Unexpected error occurred: ' + err.message);
      }

    } finally {
      clearTimeout(timeoutId);
      setLoading(false);
    }
  };

  fetchTeacherClasses();
}, [token]);

  // Timeout Effect: cancel loading if it exceeds 10 seconds
  useEffect(() => {
    const timeout = setTimeout(() => {
      if (loading) {
        setLoading(false);
        alert('Loading is taking too long. Please try again later.');
      }
    }, 20000); // 20 seconds

    return () => clearTimeout(timeout);
  }, [loading]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.student_class) {
      return alert("Please select a class");
    }

    // Validate if selected class is in the teacher's classroom list
    const isValidClass = classes.some(cls => cls.id.toString() === formData.student_class);
    if (!isValidClass) {
      return alert("Invalid class selection. You can only add students to your assigned classes.");
    }

    try {
      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Student profile created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });
    } catch (error) {
      alert('Failed to create student: ' + JSON.stringify(error.response?.data || error.message));
    }
  };

  if (loading) return <p>Loading classes... Please wait.</p>;

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="username"
        value={formData.username}
        onChange={handleChange}
        placeholder="Username"
        required
      />
      <input
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Email"
        type="email"
        required
      />
      <input
        name="password"
        value={formData.password}
        onChange={handleChange}
        placeholder="Password"
        type="password"
        required
      />
      <input
        name="date_of_birth"
        value={formData.date_of_birth}
        onChange={handleChange}
        placeholder="Date of Birth"
        type="date"
        required
      />
      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
        <option value="O">Other</option>
      </select>
      <textarea
        name="address"
        value={formData.address}
        onChange={handleChange}
        placeholder="Address"
        required
      />
      <input
        name="parent_contact"
        value={formData.parent_contact}
        onChange={handleChange}
        placeholder="Parent Contact"
        required
      />

      <label>
        Select Class:
        <select
          name="student_class"
          value={formData.student_class}
          onChange={handleChange}
          required
        >
          <option value="">-- Select Class --</option>
          {classes.map(cls => (
            <option key={cls.id} value={cls.id}>
              {cls.name}
            </option>
          ))}
        </select>
      </label>

      <button type="submit">Add Student</button>
    </form>
  );
};

export default AddStudent;
