import React, { useState } from 'react';
import axios from 'axios';
import { isTokenExpired, getTokenRemainingTime } from '../authUtils';

const allClasses = [
  { id: 1, name: 'UKG' },
  { id: 2, name: 'Class 1' },
  { id: 3, name: 'Class 2' },
  { id: 4, name: 'Class 3' },
  { id: 5, name: 'Class 4' },
  { id: 6, name: 'Class 5' },
  { id: 7, name: 'Class 6' },
  { id: 8, name: 'Class 7' },
  { id: 9, name: 'Class 8' },
  { id: 10, name: 'Class 9' },
  { id: 11, name: 'Class 10' },
];

const AddStudent = ({ token }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });

  const [classAllowed, setClassAllowed] = useState(true);
  const [loading, setLoading] = useState(false);

  const getValidToken = () => {
    const storedToken = localStorage.getItem('token') || token;
    if (!storedToken || isTokenExpired(storedToken)) {
      alert("üîê Session expired or token not found. Please login again.");
      localStorage.removeItem('token');
      window.location.href = '/TeacherDashboard';
      return null;
    }
    console.log(`‚è≥ Token valid. Remaining: ${Math.round(getTokenRemainingTime(storedToken))} sec`);
    return storedToken;
  };

  const handleChange = async (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (name === "student_class" && value !== "") {
      const validToken = getValidToken();
      if (!validToken) return;

      try {
        const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
          headers: { Authorization: `Bearer ${validToken}` }
        });

        const assignedClassId = res.data?.[0]?.id;
        if (!assignedClassesId) {
          alert("‚ö†Ô∏è No assigned class found.");
          setClassAllowed(false);
          return;
        }

        const isMatch = parseInt(value) === parseInt(assignedClassesId);
        setClassAllowed(isMatch);

        if (!isMatch) {
          alert("‚ùå You cannot assign students to this class.");
        }

      } catch (error) {
        console.error("‚ùå Error fetching assigned class:", error);
        alert("‚ùå Could not verify assigned class. Please try again.");
        setClassAllowed(false);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.student_class || !classAllowed) {
      return alert("Please select a valid class assigned to you.");
    }

    const validToken = getValidToken();
    if (!validToken) return;

    try {
      setLoading(true);
      await axios.post('http://localhost:8000/api/AddStudent/', formData, {
        headers: { Authorization: `Bearer ${validToken}` }
      });

      alert('‚úÖ Student created successfully!');
      setFormData({
        username: '',
        email: '',
        password: '',
        date_of_birth: '',
        gender: 'M',
        address: '',
        parent_contact: '',
        student_class: ''
      });
      setClassAllowed(true);
    } catch (err) {
      console.error("‚ùå Error during submission:", err);
      alert("‚ùå Failed to create student.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Student</h2>

      <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
      <input type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange} required />

      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>

      <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input type="text" name="parent_contact" placeholder="Parent Contact" value={formData.parent_contact} onChange={handleChange} required />

      <label>
        Select Class:
        <select name="student_class" value={formData.student_class} onChange={handleChange} required>
          <option value="">-- Select Class --</option>
          {allClasses.map(cls => (
            <option key={cls.id} value={cls.id}>{cls.name}</option>
          ))}
        </select>
      </label>

      {!classAllowed && (
        <p style={{ color: 'red', fontWeight: 'bold' }}>
          ‚ùå You can only assign students to your assigned class.
        </p>
      )}

      <button type="submit" disabled={loading || !classAllowed}>
        {loading ? 'Creating...' : 'Create Student'}
      </button>
    </form>
  );
};

export default AddStudent;
