import React, { useState } from 'react';
import axios from 'axios';

const AddStudent = ({ token }) => {
  const [loading, setLoading] = useState(false);
  const [classAllowed, setClassAllowed] = useState(true);

  const allClasses = [
    { id: 1, name: 'UKG' },
    { id: 2, name: 'Class 1' },
    { id: 3, name: 'Class 2' },
    { id: 4, name: 'Class 3' },
    { id: 5, name: 'Class 4' },
    { id: 6, name: 'Class 5' },
    { id: 7, name: 'Class 6' },
    { id: 8, name: 'Class 7' },
    { id: 9, name: 'Class 8' },
    { id: 10, name: 'Class 9' },
    { id: 11, name: 'Class 10' },
  ];

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    date_of_birth: '',
    gender: 'M',
    address: '',
    parent_contact: '',
    student_class: '',
  });
const handleChange = async (e) => {
  const { name, value } = e.target;
  setFormData(prev => ({ ...prev, [name]: value }));

  // Only check when class is selected
  if (name === "student_class" && value !== "") {
    try {
      const storedToken = localStorage.getItem('token') || token;

      const res = await axios.get('http://localhost:8000/api/teacher-classrooms/', {
        headers: { Authorization: `Bearer ${storedToken}` }
      });

      const assignedClassId = res.data?.[0]?.id;

      console.log("🎯 Selected class:", value);
      console.log("🏫 Assigned class from backend:", assignedClassId);

      if (assignedClassId === undefined) {
        alert("⚠️ No class assigned to you.");
        setClassAllowed(false);
        return;
      }

      const isMatch = parseInt(value) === parseInt(assignedClassId);
      setClassAllowed(isMatch);

      if (!isMatch) {
        alert("❌ You cannot assign students to this class.");
      }

    } catch (error) {
      if (error.response?.status === 401 && error.response?.data?.code === 'token_not_valid') {
        alert("❌ Session expired. Please login again.");
        localStorage.removeItem('token');
        window.location.href = '/TeacherDashboard';
        return;
      }
      console.error("❌ Error fetching class:", error.response?.data || error.message);
      alert("❌ Could not verify assigned class. Please try again.");
      setClassAllowed(false);
    }
  }
};
const handleSubmit = async (e) => {
  e.preventDefault();

  if (!formData.student_class || !classAllowed) {
    return alert("Please select a valid class assigned to you.");
  }

  try {
    const storedToken = localStorage.getItem('token') || token;

    console.log("📤 Submitting form data:", formData);
    console.log("🔐 Token used for submission:", storedToken);

    await axios.post('http://localhost:8000/api/AddStudent/', formData, {
      headers: { Authorization: `Bearer ${storedToken}` }
    });

    alert('✅ Student created!');
    setFormData({
      username: '',
      email: '',
      password: '',
      date_of_birth: '',
      gender: 'M',
      address: '',
      parent_contact: '',
      student_class: ''
    });
    setClassAllowed(true);

  } catch (err) {
    if (err.response?.status === 401 && err.response?.data?.code === 'token_not_valid') {
      alert("❌ Session expired. Please login again.");
      localStorage.removeItem('token');
      window.location.href = '/TeacherDashboard';
      return;
    }
    console.error("Error creating student:", err.response?.data || err.message);
    alert("❌ Failed to create student.");
  }
};

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Student</h2>

      <input name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
      <input name="email" placeholder="Email" type="email" value={formData.email} onChange={handleChange} required />
      <input name="password" placeholder="Password" type="password" value={formData.password} onChange={handleChange} required />
      <input name="date_of_birth" type="date" value={formData.date_of_birth} onChange={handleChange} required />

      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>

      <input name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input name="parent_contact" placeholder="Parent Contact" value={formData.parent_contact} onChange={handleChange} required />

      <label>
        Select Class:
        <select name="student_class" value={formData.student_class} onChange={handleChange} required>
          <option value="">-- Select Class --</option>
          {allClasses.map(cls => (
            <option key={cls.id} value={cls.id}>{cls.name}</option>
          ))}
        </select>
      </label>

      {!classAllowed && (
        <p style={{ color: 'red', fontWeight: 'bold' }}>
          ❌ You can only assign students to your assigned class.
        </p>
      )}

      <button type="submit" disabled={loading || !classAllowed}>
        Create Student
      </button>
    </form>
  );
};

export default AddStudent;
