import React from 'react';
// import { useHistory } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';  // Optional, for custom styles

function HomePage() {
  const navigate = useNavigate();

  const handleLoginRedirect = (role) => {
    switch (role) {
      case 'admin':
        history.push('/AdminLogin');
        break;
      case 'teacher':
        history.push('/TeacherLogin');
        break;
      case 'student':
        history.push('/StudentLogin');
        break;
      default:
        break;
    }
  };

  return (
    <div className="home-container">
      <h1>Welcome to the School Portal</h1>
      <p>Choose your role to login</p>

      <div className="login-buttons">
        <button onClick={() => handleLoginRedirect('admin')} className="login-button">
          Admin Login
        </button>
        <button onClick={() => handleLoginRedirect('teacher')} className="login-button">
          Teacher Login
        </button>
        <button onClick={() => handleLoginRedirect('student')} className="login-button">
          Student Login
        </button>
      </div>
    </div>
  );
}

export default HomePage;
